  /**
   * Merge reference for _this_ repo.
   * @type {GitHubMergeRef}
   */
  base: GitHubMergeRef

  /**
   * The User who submitted the PR
   * @type {GitHubUser}
   */
  user: GitHubUser

  /**
   * The User who is assigned the PR
   * @type {GitHubUser}
   */
